from spiderUtils import *
import re
import json
import networkx as nx
import matplotlib.pyplot as plt

def getDevelopPlan(term, major):
    term = term+"/9/1 0:00:00"
    data={
        "__EVENTTARGET": "",
        "__EVENTARGUMENT": "",
        "__VIEWSTATE": "",
        "__EVENTVALIDATION": "",
        "_ctl1:Nianji": term,
        "_ctl1:zhuanye": major,
        "_ctl1:GoSearch": "查询"
    }
    url = "http://jwc.jxnu.edu.cn/User/default.aspx?&&code=104&uctl=MyControl%5call_jxjh.ascx"
    html = postHtmlText(url, data, 'UTF-8')
    # print(html)
    DevPlan = []
    tables = re.findall(r'<table width="100%" border="1" cellspacing="0" bordercolorlight="#000000" bordercolordark="#ffffff">(.*?)</table>', html, re.S)
    for i in range(4):
        trs = re.findall(r'<tr>(.*?)</tr>', tables[i], re.S)
        for tr in trs:
            with open('./cache.json', 'w', encoding='utf-8') as f:
                f.write(json.dumps(tr, ensure_ascii=False))
            tds = re.findall(r'<td.*?>(.*?)</td>', tr, re.S)
            course = dict()
            course['type'] = tds[0].replace('\t', '').replace('\r', '').replace('\n', '').replace(' ', '').replace('&nbsp;', '')
            course['id'] = tds[1].replace('\t', '').replace('\r', '').replace('\n', '').replace(' ', '').replace('&nbsp;', '')
            course['name'] = re.search(r'<a href.*?>(.*?)</a>', tds[2], re.S).group(1).replace('\t', '').replace('\r', '').replace('\n', '').replace(' ', '').replace('&nbsp;', '')


            # 以后补上对学位课程的判断
            tds[3] = re.search(r'<a title.*?>(.*?)</a>', tds[3], re.S)
            # course['degree'] = 1 if tds[3] == '是' else 0
            # course['degree'] = tds[3]


            course['score'] = tds[5].replace('\t', '').replace('\r', '').replace('\n', '').replace(' ', '').replace('&nbsp;', '')
            course['term'] = list(tds[10].replace('\t', '').replace('\r', '').replace('\n', '').replace(' ', '').replace('&nbsp;', ''))[1]
            # print(course)
            course['pre'] = ''

            DevPlan.append(course)

    print(DevPlan)
    with open('./dev.json', 'w', encoding='utf-8') as f:
        f.write(json.dumps(DevPlan, ensure_ascii=False))


def drawDevplan(courses):
    courseTerm = [[],[],[],[],[],[],[],[]]
    for course in courses:
        term = int(course['term'])
        courseTerm[term - 1].append(course)

    nodeList = []
    posList = dict()
    edgeList = []
    for course in courseTerm:
        yPos = 0
        for i in range(len(course)):
            nodeList.append(course[i]['name'])
            # posList.append(int(courses[i]['term']), yPos)
            posList[course[i]['name']] = (int(course[i]['term'])*1.5, yPos)
            yPos += 1.5

    for course in courses:
        if course['pre'] != '':
            pre = course['pre']
            for c in courses:
                if c['id'] == pre:
                    pre = c['name']
                    edgeList.append((pre, course['name']))

    G = nx.DiGraph()
    G.add_nodes_from(nodeList)
    G.add_edges_from(edgeList)
    plt.figure(figsize=(40, 20))
    plt.title('培养方案')
    nx.draw(G, pos = posList, with_labels=True, node_color='pink', width=0.4, font_size=18)
    plt.rcParams['font.sans-serif'] = ['YouYuan']
    plt.rcParams['axes.unicode_minus'] = False
    plt.savefig('./培养方案.png')
    plt.show()

if __name__ == '__main__':
    # term = '2016'
    # major = '080902  '
    # getDevelopPlan(term, major)

    with open('./dev.json', 'r', encoding='utf-8') as f:
        courses = json.load(f)

    drawDevplan(courses)